parameters.param:
#!/usr/bin/env bash set -o nounset; set -o errexit; set -o pipefail; set -o errtrace; set -o functrace set -x
# Get project's name and version PROJECT_NAME="$(xpath -e "project/artifactId/text()" pom.xml 2>/dev/null)" PROJECT_VERSION="$(xpath -e "project/version/text()" pom.xml 2>/dev/null)"
# Set TC variables set +x echo "##teamcity[setParameter name='PROJECT_NAME' value='${PROJECT_NAME}']" echo "##teamcity[setParameter name='PROJECT_VERSION' value='${PROJECT_VERSION}']"
